import React, { Component } from 'react'

class DoubleButton extends Component {

    constructor(props) {
        super(props)

        this.state = {
            disabled: false

        }
    }

    handleClickA = () => {
        this.setState({
            disabled: !this.state.handleClickA
        })
    }

    render () {
        return (
            <div>

                <button onClick={this.state.handleClickA} disabled={this.state.handleClickA}>Bouton A</button>
            </div>
        )
    }

}
export default DoubleButton;
