import React, { Component } from 'react'
import ReactLoading from 'react-loader'

class DoubleButton extends Component {

    constructor(props) {
        super(props)

        this.state = {
            disabled: false

        }
    }

    handleClickA = () => {
        this.setState({disabled: true})
        setTimeout(() => {
            this.setState({disabled: false})

        }, 3000);
    }

    render () {
        const {disabled}= this.state
        return (
            <div>

                {disabled ? "" :<button onClick={this.handleClickA} >Bouton A</button>}
                {disabled ? <ReactLoading type="balls" color="blue"/> : ""}
                <button disabled={this.state.setTimeout}>Bouton B</button>
            </div>
        )
    }

}
export default DoubleButton;
