import React, { Component } from 'react'
import ReactLoading from 'react-loader'

class DoubleButton extends Component {
    constructor(props) {
        super(props)
        this.state = {disabled: false}
    }
    handleClickA = () => {
        this.setState({disabled: true})
        setTimeout(() => {
            this.setState({disabled: false})

        }, 5000);
    }
    render () {
        return (
            <div>
                <h1>Super bon</h1>
                <button onClick={this.handleClickA}>Bouton A</button>
               <button disabled={this.state.disabled} >Bouton B</button>
            </div>
        )
    }

}
export default DoubleButton;
