import React, { Component } from 'react'

class DoubleButton extends Component {

    constructor(props) {
        super(props)

        this.state = {
            disabled: false

        }
    }

    handleClickA = () => {
        this.setState({disabled: true})
        setTimeout(() => {
            this.setState({disabled: false})

        }, 5000);
    }

    render () {
        const {disabled}= this.state
        return (
            <div>

                <button onClick={this.handleClickA} >Bouton A</button>
                <button onClick={disabled}>Bouton B</button>
            </div>
        )
    }

}
export default DoubleButton;
